mapper.select:
SELECT b.board_id, b.user_nick, b.board_title, b.board_info, b.board_created_at, b.view_cnt, b.avg_score, p.photo_name, p.photo_uuid FROM board b LEFT JOIN photo p ON b.board_id = p.board_id
WHERE ${key} LIKE concat('%',#{word},'%')
ORDER BY ${orderBy} ${orderByDir}

mapper.update:
UPDATE board
SET view_cnt = view_cnt+1
WHERE board_id = #{boardId};

mapper.select:
SELECT photo_name
FROM photo
WHERE photo_uuid = #{fileUuid}
